import { createSlice } from "@reduxjs/toolkit"
import assets_data from "../data/assets.json";
import traits_data from "../data/traits.json";

const traits = traits_data.traits
const assets = assets_data.assets

const pageSlice = createSlice({
    name: 'Pages',
    initialState: {
        pageList: [],
        getSuccess: false,
        pageNum : 1
    },
    reducers: {
        
        createList(state, action){
            const limit = assets.length
            for (var i=0; i<limit; i+=1){
                traits.forEach(item=> {
                    if (item.id=== assets[i].id){
                        // item.push({card_element: assets[i]})
                    }
                  })
            }
            console.log(traits)
        },
        createPages(state, action){
            var size = 10; 
            const limit = assets.length
            for (var i=0; i<limit; i+=size) {
            state.pageList.push(assets_data.assets.slice(i,i+size));
            }
            state.getSuccess = true;
        }
    }
})

export const pageActions = pageSlice.actions;
export default pageSlice;
